mapper.insert:
    insert into freelancer(
        b_no
        ,p_no
        ,subject
        ,preferred_work
        ,start_time
        ,end_time
        ,start_date
        ,end_date
        ,content
        ,reg_date
        ,pwd  

		 ,img_name

    )values(
       (select nvl(max(b_no),0)+1 from freelancer)
        ,#{p_no}
        ,#{subject}
        ,#{preferred_work}
        ,#{start_time}
        ,#{end_time}
        ,#{start_date}
        ,#{end_date}
        ,#{content}
        ,sysdate
        ,#{pwd}	        

		 ,#{img_name}

    )

mapper.update:
update 
	freelancer
set	
    subject           =  #{subject}
	,preferred_work   =  #{preferred_work}
	,start_time      =  #{start_time}
	,end_time        =  #{end_time}
	,start_date      =  #{start_date}
	,end_date        =  #{end_date}
	,content		 =  #{content}


			,img_name   = #{img_name}



			,img_name   = null


  where 
		b_no=#{b_no}
	and
		pwd=#{pwd}	
	and
	    p_no=#{p_no}

mapper.delete:
delete from 
	freelancer
where 
	b_no=#{b_no}
and
	pwd=#{pwd}	
and
    p_no=#{p_no}
