mapper.insert:
  INSERT INTO role_detail (
    g_no,
    dept_code1,
    position_code1,
    field_detail1,
    content1       
    ,dept_code2      
    ,position_code2     

    ,field_detail2


    ,content2

    ,dept_code3
    ,position_code3

    ,field_detail3


    ,content3




)
VALUES (
    #{gonggo_no},
    #{dept_code1},
    #{position_code1},
    #{field_detail1},
    #{content1}


   		,null


   		,#{dept_code2}





   		,null


   		,#{position_code2}



    ,#{field_detail2}


    ,#{content2}




   		,null


   		,#{dept_code3}




   		,null


   		,#{position_code3}



    ,#{field_detail3}


    ,#{content3}


)

mapper.select:
select g.g_no ,g.c_no ,g.manager_name ,g.work_place ,g.career ,g.seeker_sex ,substr(g.seeker_age,'1','2') as "seeker_age1" ,substr(g.seeker_age,'4','2') as "seeker_age2" ,g.salary ,g.attendencetime ,g.leaveworktime ,g.graduation ,g.content ,g.opendate ,g.closedate ,g.content ,g.ph_d_candidate ,b.choice1 ,b.choice2 ,b.choice3 ,b.choice4 ,b.choice5 ,b.benefit_code1 ,b.benefit_code2 ,b.benefit_code3 ,b.benefit_code4 ,b.benefit_code5 ,r.dept_code1 ,r.dept_code2 ,r.dept_code3 ,r.position_code1 ,r.position_code2 ,r.position_code3 ,r.content1 ,r.content2 ,r.content3
,(select c.name from gonggo g, company_mem c where g.c_no=c.c_no and g.g_no=#{g_no}) as "name" ,(select i.volume from gonggo g, company_mem m, company_info i where g.c_no=m.c_no and m.c_no=i.c_no and g.g_no=#{g_no}) as "volume" ,replace(r.field_detail1, ',','') as "field_detail1" ,replace(r.field_detail2, ',','') as "field_detail2" ,replace(r.field_detail3, ',','') as "field_detail3" ,(select LISTAGG( c.welfare_name,', ') WITHIN GROUP (ORDER BY c.welfare_name) from gonggo g, company_mem m, company_welfare w, code_welfare c where g.c_no=m.c_no and m.c_no=w.c_no and w.welfare_code= c.welfare_code and g.g_no=#{g_no} ) as "welfare_name" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code1=c.process_code and g.g_no=#{g_no} and p.order_no1 = 1) as "process_name1" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code2=c.process_code and g.g_no=#{g_no} and p.order_no2 = 2) as "process_name2" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code3=c.process_code and g.g_no=#{g_no} and p.order_no3 = 3) as "process_name3" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code4=c.process_code and g.g_no=#{g_no} and p.order_no4 = 4) as "process_name4" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code5=c.process_code and g.g_no=#{g_no} and p.order_no5 = 5) as "process_name5" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code6=c.process_code and g.g_no=#{g_no} and p.order_no6 = 6) as "process_name6" ,(select c.process_name from gonggo g, gonggo_process p, code_process c where g.g_no=p.g_no and p.process_code7=c.process_code and g.g_no=#{g_no} and p.order_no7 = 7) as "process_name7" from
gonggo g, benefit_code b, role_detail r where g.g_no = b.g_no and g.g_no = r.g_no and g.g_no = #{g_no}

mapper.update:
