manager_name=#{manager_name}
,career=#{career}
,seeker_sex=#{seeker_sex}
,seeker_age=#{seeker_age}

,salary=#{salary}
,opendate=#{opendate}
,closedate=#{closedate}
,graduation=#{graduation}
,content=#{content}


		,ph_d_candidate=#{ph_d_candidate}


		,ph_d_candidate=null




where
g_no=#{g_no}

update
role_detail
set


		dept_code1=null


		dept_code1=#{dept_code1}




		,dept_code2=null


		,dept_code2=#{dept_code2}




		,dept_code3=null


		,dept_code3=#{dept_code3}





		,position_code1=null


		,position_code1=#{position_code1}




		,position_code2=null


		,position_code2=#{position_code2}




		,position_code3=null


		,position_code3=#{position_code3}


,content1=#{content1}
,content2=#{content2}
,content3=#{content3}



























		,field_detail1=null


		,field_detail1=#{field_detail1}




		,field_detail2=null


		,field_detail2=#{field_detail2}




		,field_detail3=null


		,field_detail3=#{field_detail3}





where
g_no=#{g_no}

mapper.select:
