mapper.insert:

    insert into project (
        prj_no
        ,p_no
        ,subject
        ,project_type
        ,field_code
        ,people_to
        ,start_time
        ,end_time
        ,skill_name
        ,content
        ,pwd

        ,read_count
        ,reg_date


		  ,img_name


    ) values (
        (select nvl(max(prj_no),0)+1 from project)
        ,#{p_no}
        ,#{subject}
        ,#{project_type}
        ,#{field_code}
        ,#{people_to}
        ,#{start_time}
        ,#{end_time}
        ,#{skill_name}
        ,#{content}
        ,#{pwd}
        ,0
        ,sysdate

		 ,#{img_name}



    )

mapper.update:

update
	project
set
	read_count = read_count+1
where
	prj_no=#{prj_no}
	and
	pwd=#{pwd}
	and
	p_no=#{p_no}

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select prj_no ,subject ,project_type ,to_char(reg_date, 'MM-DD') as "reg_date" ,to_char(start_time, 'YYYY-MM-DD') as "start_time" ,to_char(end_time, 'YYYY-MM-DD') as "end_time" ,read_count from project where 1=1
and to_number(to_char(end_time,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))>0 and
to_number(to_char(start_time,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))<0
and ( upper(subject) like upper('%${keyword}%') or upper(content) like upper('%${keyword}%') )
and( upper(project_type) like upper('%${project_type}%') )
order by prj_no desc
order by ${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.update:

update
	project
set
	subject = #{subject}
	,field_code = #{field_code}
	,project_type = #{project_type}
	,people_to = #{people_to}
	,start_time = #{start_time}
	,end_time = #{end_time}
	,content = #{content}


		,img_name   = #{img_name}



		,img_name   = null


where
	prj_no = #{prj_no}
	and
	pwd = #{pwd}
	and
	p_no=#{p_no}

mapper.delete:

delete from
	project
where
	prj_no = #{prj_no}
	and
	pwd = #{pwd}
	and
	p_no=#{p_no}

mapper.insert:
    insert into competition(
        comp_pk
        ,c_no
        ,subject
        ,field_code
        ,start_time
        ,end_time
        ,content
        ,pwd
        ,read_count
        ,reg_date

		  ,img_name

    ) values (
        (select nvl(max(comp_pk),0)+1 from competition)
        ,#{c_no}
        ,#{subject}
        ,#{field_code}
        ,#{start_time}
        ,#{end_time}
        ,#{content}
        ,#{pwd}
        ,0
        ,sysdate

		 ,#{img_name}

    )

mapper.update:

update
	competition
set
	subject = #{subject}
	,start_time = #{start_time}
	,end_time = #{end_time}
	,field_code = #{field_code}
	,content = #{content}

		,img_name   = #{img_name}



		,img_name   = null


where
	comp_pk = #{comp_pk}
	and
	pwd = #{pwd}

mapper.delete:

delete from
	competition
where
	comp_pk = #{comp_pk}
	and
	pwd = #{pwd}
