mapper.insert:
insert into part_time(
    b_no
	,p_no
	,subject
    ,hope_work
    ,start_time
    ,end_time
    ,start_date
    ,end_date
    ,content
    ,reg_date
    ,pwd

		 ,img_name

)values(
    (select nvl(max(b_no),0)+1 from part_time)
    ,#{p_no}
    ,#{subject}
    ,#{hope_work}
    ,#{start_time}
    ,#{end_time}
    ,#{start_date}
    ,#{end_date}
    ,#{content}
    ,sysdate
    ,#{pwd}

		 ,#{img_name}


)

mapper.update:
update 
	part_time
set	
     subject     = #{subject}
	,hope_work   = #{hope_work}
	,start_time  = #{start_time}
	,end_time    = #{end_time}
	,start_date  = #{start_date}
	,end_date    = #{end_date}
	,content     = #{content}


		,img_name   = #{img_name}



		,img_name   = null



where 
	b_no=#{b_no}
and
	pwd=#{pwd}

mapper.delete:
delete from 
	part_time
where 
	b_no=#{b_no}
and
	pwd=#{pwd}	
and
    p_no=#{p_no}
