mapper.insert:

	(select nvl(max(c_no),0)+1 from company_mem)

insert into company_mem(
c_no
 ,cid
 ,pwd
 ,business_no
 ,call
 ,ceo_name
 ,ceo_phone
 ,name
 ,email
 ,reg_date
) values (
	#{mem_c_no}
	,#{cid}
	,#{pwd}
	,#{business_no}
	,#{call}
	,#{ceo_name}
	,#{ceo_phone}
	,#{name}
	,#{email}||#{email2}
	,sysdate
	)

insert into company_welfare

	select
		#{mem_c_no}
		,#{tmp}
	from dual

      insert  into person_mem
      (
	      p_no
	    ,name
	    ,pid
	    ,pwd
	    ,phone
	    ,email
	    ,field_code
	    ,is_job
	    ,nickname
	    ,addr
	    ,sex
	    ,jumin_num
	    ,reg_date
      )
       values (
         (select nvl(max(p_no),0)+1 from person_mem)
         ,#{name}
         ,#{pid}
         ,#{pwd}
         ,#{phone}
         ,#{email1}||''|| #{email2}
         ,${field_code}
         ,#{is_job}
         ,#{nickname}
         ,#{addr1} ||' '|| #{addr2} ||' '||#{addr3}
         ,#{sex}
         ,#{jumin_num1} ||''|| #{jumin_num2}
         ,sysdate
         )
