mapper.select:
SELECT r.resume_no, p.p_no, p.name , p.addr , p.phone, a.name1, a.type1, a.name2, a.type2, a.name3, a.type3, a.name4, a.type4, ca.career1, ca.career2, ca.career3, ca.career4, ca.career5, l.license_name1, l.license_name2, l.license_name3, l.license_name4, l.license_name5, p.email, c.field_name, p.sex, CASE WHEN SUBSTR(p.jumin_num, 7, 1) = '1' THEN '19' WHEN SUBSTR(p.jumin_num, 7, 1) = '2' THEN '19' ELSE '20' END || SUBSTR(p.jumin_num, 1, 2) || '-' || SUBSTR(p.jumin_num, 3, 2) || '-' || SUBSTR(p.jumin_num, 5, 2) AS birth, TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - TO_NUMBER( CASE WHEN SUBSTR(p.jumin_num, 7, 1) = '1' THEN '19' WHEN SUBSTR(p.jumin_num, 7, 1) = '2' THEN '19' ELSE '20' END || SUBSTR(p.jumin_num, 1, 2) ) AS age, p.is_job, r.married, r.military, r.hope_salary, r.introduce, e.school_name, e.education, to_char(e.enter_date,'YYYY') as enter_date, to_char(e.graduation_date,'YYYY"년" MM"월"') as graduation_date FROM resume r
JOIN person_mem p ON p.p_no = r.p_no JOIN code_field c ON p.field_code = c.field_code LEFT JOIN awards a ON r.resume_no = a.resume_no LEFT JOIN career ca ON r.resume_no = ca.resume_no LEFT JOIN person_license l on r.resume_no=l.resume_no LEFT JOIN education e on r.resume_no=e.resume_no
WHERE p.p_no = #{p_no}

SELECT COUNT(*) 
   FROM comtoper 
   WHERE resume_no = #{resume_no} 
     AND c_no = #{c_no}
